INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('TRUNCATE', false, '', 'Data is too long for text or varchar field', 'Truncate data to required size and load');
INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('INVALIDDATE', false, '', 'Data in a date or timestamp column cannot be parsed as a date', 'Ignore and load as-is');
INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('NONNUMERIC', false, '', 'Data in a numeric or decimal column is not a number', 'Ignore and load as-is');
INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('MISSING', false, '', 'Data is missing in a field defined as required by the schema', 'Ignore and load the blank data');
INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('UNKNOWNCOLUMNTYPE', false, '', 'A column defined in the schema has an invalid column type', 'Ignore and default to varchar');
INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('COLUMNNOTINSCHEMA', true, '', 'A column is present in a data file but not mentioned in the schema', 'Ignore and don''t load the column');
INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('MISSINGATTRIBUTE', true, '', 'A column in the schema has a missing length, precision or scale', 'Ignore and use defaults for validation');
INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('TOOFEWCOLUMNS', true, '', 'A row in a data file has fewer columns than declared by the schema', 'Ignore and load the missing columns as blank');
INSERT INTO cz.validation_error_handler(name, allow, parameters, description, allow_message) VALUES ('TOOMANYCOLUMNS', true, '', 'A row in a data file has more columns than declared by the schema', 'Ignore and load only the columns mentioned by the schema');